  // Bulk Duplicate Handler for Slots
  const handleBulkDuplicate = async (entityType) => {
    console.log('ðŸ”„ BULK DUPLICATE CLICKED:', entityType);
    console.log('ðŸ”„ Selected items:', selectedItems);

    if (selectedItems.length === 0) {
      showCustomNotification('âŒ Please select items to duplicate first!', 'error');
      return;
    }

    if (entityType !== 'slots') {
      showCustomNotification('âŒ Duplication is only available for slots!', 'error');
      return;
    }

    try {
      console.log('ðŸš€ Starting bulk duplicate...');
      const token = localStorage.getItem('token');

      // Get all existing slot serial numbers to avoid conflicts
      const existingSerials = slotMachines.map(slot => slot.serial_number);
      console.log('ðŸ“‹ Existing serials:', existingSerials);

      let successCount = 0;
      for (let i = 0; i < selectedItems.length; i++) {
        const itemId = selectedItems[i];
        console.log(`ðŸ”„ Duplicating ${itemId}...`);

        // Find the original slot
        const originalSlot = slotMachines.find(slot => slot.id === itemId);
        if (!originalSlot) {
          console.error(`âŒ Original slot not found: ${itemId}`);
          continue;
        }

        // Generate new serial number
        const baseSerial = originalSlot.serial_number;
        let newSerial = `${baseSerial}-1`;
        let counter = 1;

        // Find next available serial number
        while (existingSerials.includes(newSerial)) {
          counter++;
          newSerial = `${baseSerial}-${counter}`;
        }

        // Add new serial to existing list to avoid conflicts in same batch
        existingSerials.push(newSerial);

        // Create duplicate slot data
        const duplicateData = {
          ...originalSlot,
          serial_number: newSerial,
          status: 'active' // Set to active by default
        };

        // Remove id and created_at to create new record
        delete duplicateData.id;
        delete duplicateData.created_at;
        delete duplicateData.created_by;

        console.log('ðŸ“¤ Creating duplicate with data:', duplicateData);

        const response = await fetch(`${API}/slot-machines`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify(duplicateData)
        });

        if (response.ok) {
          successCount++;
          console.log(`âœ… Duplicated ${originalSlot.serial_number} -> ${newSerial}`);
        } else {
          const errorText = await response.text();
          console.error(`âŒ Failed to duplicate ${originalSlot.serial_number}:`, errorText);
        }
      }

              showCustomNotification(`âœ… Successfully duplicated ${successCount} of ${selectedItems.length} slots!`, 'success');
      await fetchDashboardData();
      setSelectedItems([]);
      console.log('ðŸ”„ Data refreshed, selections cleared');

    } catch (error) {
      console.error('ðŸ’¥ Bulk duplicate error:', error);
              showCustomNotification('âŒ Bulk duplicate failed: ' + error.message, 'error');
    }
  };
