              </div>
            </div>
          </div>
        )}

        {/* Edit Date/Time Modal */}
        {editDateTimeModal.isOpen && (
          <div style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 1000
          }}>
            <div style={{
              backgroundColor: 'var(--bg-primary)',
              borderRadius: '8px',
              padding: '24px',
              maxWidth: '400px',
              width: '90%',
              boxShadow: '0 4px 20px rgba(0, 0, 0, 0.3)',
              border: '1px solid var(--border-color)'
            }}>
              <div style={{
                display: 'flex',
                alignItems: 'center',
                marginBottom: '16px'
              }}>
                <span style={{ fontSize: '24px', marginRight: '12px' }}>â°</span>
                <h3 style={{
                  margin: 0,
                  color: 'var(--text-primary)',
                  fontSize: '18px',
                  fontWeight: 'bold'
                }}>
                  Edit Scheduled Date/Time
                </h3>
              </div>

              <div style={{
                color: 'var(--text-secondary)',
                marginBottom: '24px',
                lineHeight: '1.5'
              }}>
                <p>Currently scheduled for: <strong>{editDateTimeModal.scheduledChange?.scheduledDateTime}</strong></p>
                <p>Select new date and time:</p>
              </div>

              <div style={{ marginBottom: '24px' }}>
                <input
                  type="datetime-local"
                  value={editDateTimeModal.newDateTime}
                  onChange={(e) => setEditDateTimeModal(prev => ({ ...prev, newDateTime: e.target.value }))}
                  style={{
                    width: '100%',
                    padding: '12px',
                    border: '1px solid var(--border-color)',
                    borderRadius: '4px',
                    fontSize: '14px',
                    backgroundColor: 'var(--bg-secondary)',
                    color: 'var(--text-primary)'
                  }}
                />
              </div>

              <div style={{
                display: 'flex',
                justifyContent: 'flex-end',
                gap: '12px'
              }}>
                <button
                  onClick={editDateTimeModal.onCancel}
                  style={{
                    padding: '8px 16px',
                    border: '1px solid var(--border-color)',
                    borderRadius: '4px',
                    backgroundColor: 'transparent',
                    color: 'var(--text-primary)',
                    cursor: 'pointer',
                    fontSize: '14px'
                  }}
                >
                  Cancel
                </button>
                <button
                  onClick={() => editDateTimeModal.onSave(editDateTimeModal.newDateTime)}
                  style={{
                    padding: '8px 16px',
                    border: 'none',
                    borderRadius: '4px',
                    backgroundColor: '#17a2b8',
                    color: 'white',
                    cursor: 'pointer',
                    fontSize: '14px',
                    fontWeight: 'bold'
                  }}
                >
                  Save Changes
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Report Details Modal */}
        {showReportDetailsModal && selectedReportDetails && (
          <ReportDetailsModal 
            report={selectedReportDetails}
            onClose={() => {
              setShowReportDetailsModal(false);
              setSelectedReportDetails(null);
            }}
          />
        )}
    </div>
  );
};
// Main App Component
const App = () => {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
};

const AppContent = () => {
  const { user, loading } = useAuth();

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading application...</p>
      </div>
    );
  }

  return (
    <div className="App">
      {user ? <Dashboard /> : <Login />}
    </div>
  );
};

export default App;

// Adaug componenta GameMixAvatar sub AvatarDisplay
function GameMixAvatar({ entityId, name }) {
  const { avatar, loading } = useAvatar('game_mixes', entityId);
  if (loading) return <div style={{ width: 20, height: 95 }} />;
  if (avatar) {
    return <img src={`data:${avatar.mime_type};base64,${avatar.file_data}`} alt="Avatar" style={{ width: 20, height: 95, objectFit: 'cover', borderRadius: 4 }} />;
  }
  // Fallback: background albastru cu text alb, font fix

  return (
    <div style={{ 
      width: 20, 
      height: 95, 
      background: '#1976d2', 
      color: '#ffffff', 
      fontWeight: 'bold', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center', 
      borderRadius: 4, 
      lineHeight: '1.1'
    }}>
      {name}
    </div>
  );
}
